Layer.OnCommand:
%function(self)
			bg_bg1 = self;
			self:diffusealpha(0);
			self:xy(SCREEN_CENTER_X, SCREEN_CENTER_Y);
			self:zoom(1);
		end

%function(self)
			bg_bg2 = self;
			self:diffusealpha(0);
			self:xy(SCREEN_CENTER_X, SCREEN_CENTER_Y);
			self:zoom(1);
		end

%function(self)
			bg_star = self;
			self:diffusealpha(0);
			self:xy(SCREEN_CENTER_X, SCREEN_CENTER_Y);
			self:rotationz(-30);
			self:zoom(2);
		end
